  import React, { useState, useEffect } from 'react';
  import './eventslist.css';
  import { request } from '../../util/fetchAPI2'; // Import the request function
  import { loadStripe } from '@stripe/stripe-js';
  const stripe = await loadStripe(process.env.STRIPE_PRIVATE_KEY);


  const EventsList = () => {
      const [events, setEvents] = useState([]);

      useEffect(() => {
          const fetchEvents = async () => {
              try {
                  const response = await request('/api3/events', 'GET');
                  if (Array.isArray(response)) {
                      setEvents(response);
                  } else {
                      console.error('Invalid response format:', response);
                  }
              } catch (error) {
                  console.error('Error:', error.message);
              }
          };

          fetchEvents();
      }, []);
      const handlePayment = async (eventId, regnfees) => {
        try {
            const stripe = await loadStripe('process.env.STRIPE_PRIVATE_KEY');
            const amount = parseFloat(regnfees);

            const response = await request(`/api3/payments/${eventId}`, 'POST', {
                amount: amount,
            });

            // Redirect to Stripe Checkout or handle the payment flow with Stripe
            // Use Stripe's APIs or components here for payment
            if (response.success) {
                const result = await stripe.redirectToCheckout({
                    sessionId: response.sessionId,
                });
                if (result.error) {
                    console.error('Error redirecting to checkout:', result.error);
                }
            }
        } catch (error) {
            console.error('Error:', error.message);
        }
    };

    return (
      <div className="events-list-container">
        {events.map((event) => (
          <div key={event._id} className="event-item">
            <h2>{event.eventType}</h2>
            <p>Event Name: {event.eventName}</p>
            <p>College Name: {event.collegeName}</p>
            <img src={`http://localhost:5000/${event.posterimg}`} alt="Poster" className="event-poster" />
            <p>Description: {event.description}</p>
            <p>Cash Prize: {event.cashPrize}</p>
            <p>Registeration Fees: {event.regnfees}</p>
            <p>Website: <a href={event.website}>{event.website}</a></p>
            <button onClick={() => handlePayment(event._id, event.regnfees)}>
                          Pay and Register
                      </button>
            

          </div>
        ))}
      </div>
    );
  };

  export default EventsList;
