import React, { useState, useEffect } from 'react';
import './eventslist.css';
import { request } from '../../util/fetchAPI2'; // Import the request function

const EventsList = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Fetch events from the backend
    const fetchEvents = async () => {
      try {
        const response = await request('/api3/events', 'GET'); // Assuming '/api2' is the route to your backend
        setEvents(response); // Update state with the fetched events
      } catch (error) {
        console.error('Error:', error.message);
        // Handle error state or display error message
      }
    };

    fetchEvents();
  }, []); // Fetch events only once when the component mounts

  return (
    <div className="events-list-container">
      {events.map((event) => (
        <div key={event._id} className="event-item">
          <h2>{event.eventType}</h2>
          <p>Event Name: {event.eventName}</p>
          <p>College Name: {event.collegeName}</p>
          <img src={event.posterimg} alt="Poster" className="event-poster" />
          <p>Description: {event.description}</p>
          <p>Cash Prize: {event.cashPrize}</p>
          <p>Website: <a href={event.website}>{event.website}</a></p>
        </div>
      ))}
    </div>
  );
};

export default EventsList;
