import React, { useState } from 'react';
import './organize.css'; // Your CSS file for Organize component
import {Link} from 'react-router-dom';
import { request } from '../../util/fetchAPI'; // Import the request function

const Organize = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    college: '',
    idcard: '',
    phoneNumber: '',
  });
  const handleImageChange = (e) => {
    // Handle image upload using multer or other image processing library
    const file = e.target.files[0];
    // Process the file as needed
    console.log('Uploaded image:', file);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Form Data:', formData); // Log the form data
      const response = await request('/api2/organize', 'POST', {}, formData);
      console.log('Response:', response);
      // Dispatch any actions if needed
      // dispatch(someAction());
    } catch (error) {
      console.error('Error:', error.message);
      // Handle error state or display error message
    }
  };

  return (
    <div className="organize-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="college">College:</label>
          <input
            type="text"
            id="college"
            name="college"
            value={formData.college}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="posterImage">Id card/Proof of Organizer</label>
          <input
            type="file"
            id="posterImage"
            name="posterImage"
            onChange={handleImageChange}
            accept="image/*"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="tel"
            id="phoneNumber"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
      <span>Already have a login?  </span>
      <Link  to = '/login'> <span className='loginbtn'>Login</span></Link>
    </div>
  );
};

export default Organize;
