import React, { useState } from 'react';
import './createevent.css'; // Your CSS file for CreateEvent component

const CreateEvent = () => {
  const [formData, setFormData] = useState({
    eventType: '',
    eventName: '',
    collegeName: '',
    cashPrize: '',
    description: '',
    onlineOffline: '',
    website: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    // Handle image upload using multer or other image processing library
    const file = e.target.files[0];
    // Process the file as needed
    console.log('Uploaded image:', file);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission - You can perform actions here with formData
    console.log(formData);
  };

  return (
    <div className="create-event-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="eventType">Type of Event:</label>
          <input
            type="text"
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="eventType">Event Name:</label>
          <input
            type="text"
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="eventType">College Name</label>
          <input
            type="text"
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          />
        </div>

        {/* ... Other form fields */}
        <div className="form-group">
          <label htmlFor="posterImage">Poster Image</label>
          <input
            type="file"
            id="posterImage"
            name="posterImage"
            onChange={handleImageChange}
            accept="image/*"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="cashPrize">Cash Prize</label>
          <input
            type="text"
            id="cashPrize"
            name="cashPrize"
            value={formData.cashPrize}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="eventType">Description about the event:</label>
          <input
            type="text"
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="eventType">Website link</label>
          <input
            type="text"
            id="eventType"
            name="eventType"
            value={formData.eventType}
            onChange={handleChange}
            required
          />
        </div>
        {/* ... Other form fields */}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default CreateEvent;
